#!/bin/bashsource /etc/hysteria/core/scripts/utils.shdefine_colorsCADDY_CONFIG_FILE_NORMALSUB="/etc/hysteria/core/scripts/normalsub/Caddyfile.normalsub"NORMALSUB_ENV_FILE="/etc/hysteria/core/scripts/normalsub/.env"DEFAULT_AIOHTTP_LISTEN_ADDRESS="127.0.0.1"DEFAULT_AIOHTTP_LISTEN_PORT="28261"install_caddy_if_needed() {    if command -v caddy &> /dev/null; then        echo -e "${green}Caddy уже установлен.${NC}"        if systemctl list-units --full -all | grep -q 'caddy.service'; then            if systemctl is-active --quiet caddy.service; then                echo -e "${yellow}Остановка и отключение службы caddy по умолчанию...${NC}"                systemctl stop caddy > /dev/null 2>&1                systemctl disable caddy > /dev/null 2>&1            fi        fi        return 0    fi    echo -e "${yellow}Установка Caddy...${NC}"    sudo apt update -y > /dev/null 2>&1    sudo apt install -y debian-keyring debian-archive-keyring apt-transport-https curl > /dev/null 2>&1    curl -1sLf 'https://dl.cloudsmith.io/public/caddy/stable/gpg.key' | sudo gpg --dearmor -o /usr/share/keyrings/caddy-stable-archive-keyring.gpg > /dev/null 2>&1    curl -1sLf 'https://dl.cloudsmith.io/public/caddy/stable/debian.deb.txt' | sudo tee /etc/apt/sources.list.d/caddy-stable.list > /dev/null 2>&1    chmod o+r /usr/share/keyrings/caddy-stable-archive-keyring.gpg    chmod o+r /etc/apt/sources.list.d/caddy-stable.list    sudo apt update -y > /dev/null 2>&1    sudo apt install -y caddy    if [ $? -ne 0 ]; then        echo -e "${red}Ошибка: Не удалось установить Caddy. ${NC}"        exit 1    fi    systemctl stop caddy > /dev/null 2>&1    systemctl disable caddy > /dev/null 2>&1    echo -e "${green}Caddy успешно установлен. ${NC}"}update_env_file() {    local domain=$1    local external_port=$2    local aiohttp_listen_address=$3    local aiohttp_listen_port=$4    local subpath_val=$(pwgen -s 32 1)    cat <<EOL > "$NORMALSUB_ENV_FILE"HYSTERIA_DOMAIN=$domainHYSTERIA_PORT=$external_portAIOHTTP_LISTEN_ADDRESS=$aiohttp_listen_addressAIOHTTP_LISTEN_PORT=$aiohttp_listen_portSUBPATH=$subpath_valEOL}update_caddy_file_normalsub() {    local domain=$1    local external_port=$2    local subpath_val=$3    local aiohttp_address=$4    local aiohttp_port=$5    cat <<EOL > "$CADDY_CONFIG_FILE_NORMALSUB"# Global configuration{    admin off    auto_https disable_redirects}$domain:$external_port {    encode gzip zstd        route /$subpath_val/* {        reverse_proxy $aiohttp_address:$aiohttp_port {            header_up X-Real-IP {remote_host}            header_up X-Forwarded-For {remote_host}            header_up X-Forwarded-Port {server_port}            header_up X-Forwarded-Proto {scheme}        }    }    @blocked {        not path /$subpath_val/*    }    abort @blocked}EOL}create_normalsub_python_service_file() {    cat <<EOL > /etc/systemd/system/hysteria-normal-sub.service[Unit]Description=Служба подписок Hysteria PythonAfter=network.target[Service]ExecStart=/bin/bash -c 'source /etc/hysteria/hysteria2_venv/bin/activate && /etc/hysteria/hysteria2_venv/bin/python /etc/hysteria/core/scripts/normalsub/normalsub.py'WorkingDirectory=/etc/hysteria/core/scripts/normalsubEnvironmentFile=$NORMALSUB_ENV_FILERestart=alwaysUser=rootGroup=root[Install]WantedBy=multi-user.targetEOL}create_caddy_normalsub_service_file() {    cat <<EOL > /etc/systemd/system/hysteria-caddy-normalsub.service[Unit]Description=Caddy для подписок HysteriaAfter=network.target[Service]WorkingDirectory=/etc/hysteria/core/scripts/normalsubExecStart=/usr/bin/caddy run --environ --config $CADDY_CONFIG_FILE_NORMALSUBExecReload=/usr/bin/caddy reload --config $CADDY_CONFIG_FILE_NORMALSUB --forceTimeoutStopSec=5sLimitNOFILE=1048576PrivateTmp=trueUser=rootGroup=root[Install]WantedBy=multi-user.targetEOL}start_service() {    local domain=$1    local external_port=$2    install_caddy_if_needed    local aiohttp_listen_address="$DEFAULT_AIOHTTP_LISTEN_ADDRESS"    local aiohttp_listen_port="$DEFAULT_AIOHTTP_LISTEN_PORT"    update_env_file "$domain" "$external_port" "$aiohttp_listen_address" "$aiohttp_listen_port"    source "$NORMALSUB_ENV_FILE" # To get SUBPATH for Caddyfile    update_caddy_file_normalsub "$HYSTERIA_DOMAIN" "$HYSTERIA_PORT" "$SUBPATH" "$AIOHTTP_LISTEN_ADDRESS" "$AIOHTTP_LISTEN_PORT"        create_normalsub_python_service_file    create_caddy_normalsub_service_file    systemctl daemon-reload        systemctl enable hysteria-normal-sub.service > /dev/null 2>&1    systemctl start hysteria-normal-sub.service        systemctl enable hysteria-caddy-normalsub.service > /dev/null 2>&1    systemctl start hysteria-caddy-normalsub.service    if systemctl is-active --quiet hysteria-normal-sub.service && systemctl is-active --quiet hysteria-caddy-normalsub.service; then        echo -e "${green}Настройка службы Подписок завершена.${NC}"        echo -e "${green}Базовый URL доступа: https://$HYSTERIA_DOMAIN:$HYSTERIA_PORT/$SUBPATH/sub/normal/{username}${NC}"    else        echo -e "${red}Настройка Подписок завершена, но одна или несколько служб не запустились.${NC}"        systemctl status hysteria-normal-sub.service --no-pager        systemctl status hysteria-caddy-normalsub.service --no-pager    fi}stop_service() {    echo -e "${yellow}Остановка службы Подписок для Hysteria...${NC}"    systemctl stop hysteria-normal-sub.service > /dev/null 2>&1    systemctl disable hysteria-normal-sub.service > /dev/null 2>&1    echo -e "${yellow}Остановка службы Caddy для Подписок...${NC}"    systemctl stop hysteria-caddy-normalsub.service > /dev/null 2>&1    systemctl disable hysteria-caddy-normalsub.service > /dev/null 2>&1        systemctl daemon-reload > /dev/null 2>&1    rm -f "$NORMALSUB_ENV_FILE"    rm -f "$CADDY_CONFIG_FILE_NORMALSUB"    rm -f /etc/systemd/system/hysteria-normal-sub.service    rm -f /etc/systemd/system/hysteria-caddy-normalsub.service    systemctl daemon-reload > /dev/null 2>&1    echo -e "${green}Службы Подписки остановлены и отключены. Конфигурационные файлы удалены.${NC}"}edit_subpath() {    local new_path="$1"        if [ ! -f "$NORMALSUB_ENV_FILE" ]; then        echo -e "${red}Ошибка: Файл .env ($NORMALSUB_ENV_FILE) не найден. Пожалуйста, сначала выполните команду start.${NC}"        exit 1    fi    if [[ ! "$new_path" =~ ^[a-zA-Z0-9]+$ ]]; then        echo -e "${red}Ошибка: Новый подпуть должен содержать только буквенно-цифровые символы (a-z, A-Z, 0-9) и не может быть пустым.${NC}"        exit 1    fi    if ! systemctl is-active --quiet hysteria-normal-sub.service || ! systemctl is-active --quiet hysteria-caddy-normalsub.service; then        echo -e "${red}Ошибка: Одна или несколько служб не запущены. Пожалуйста, сначала запустите службы.${NC}"        exit 1    fi    source "$NORMALSUB_ENV_FILE"    local old_subpath="$SUBPATH"        sed -i "s|^SUBPATH=.*|SUBPATH=$new_path|" "$NORMALSUB_ENV_FILE"    echo -e "${green}SUBPATH обновлен с '$old_subpath' на '$new_path' в $NORMALSUB_ENV_FILE.${NC}"    update_caddy_file_normalsub "$HYSTERIA_DOMAIN" "$HYSTERIA_PORT" "$new_path" "$AIOHTTP_LISTEN_ADDRESS" "$AIOHTTP_LISTEN_PORT"    echo -e "${green}Caddyfile для Подписок обновлен с новым подпутем.${NC}"    echo -e "${yellow}Перезапуск службы подписок hysteria для перезагрузки окружения...${NC}"    systemctl restart hysteria-normal-sub.service    echo -e "${yellow}Перезагрузка конфигурации Caddy...${NC}"    if systemctl reload hysteria-caddy-normalsub.service 2>/dev/null; then        echo -e "${green}Конфигурация Caddy успешно перезагружена.${NC}"    else        echo -e "${yellow}Перезагрузка не удалась, перезапуск службы Caddy...${NC}"        systemctl restart hysteria-caddy-normalsub.service    fi    if systemctl is-active --quiet hysteria-normal-sub.service && systemctl is-active --quiet hysteria-caddy-normalsub.service; then        echo -e "${green}Службы успешно обновлены.${NC}"        echo -e "${green}Новый базовый URL доступа: https://$HYSTERIA_DOMAIN:$HYSTERIA_PORT/$new_path/sub/normal/{username}${NC}"        echo -e "${cyan}Старый подпуть '$old_subpath' больше не доступен.${NC}"    else        echo -e "${red}Ошибка: Не удалось перезапустить/перезагрузить одну или несколько служб. Проверьте логи.${NC}"        systemctl status hysteria-normal-sub.service --no-pager        systemctl status hysteria-caddy-normalsub.service --no-pager    fi}case "$1" in    start)        if [ -z "$2" ] || [ -z "$3" ]; then            echo -e "${red}Использование: $0 start <EXTERNAL_DOMAIN> <EXTERNAL_PORT>${NC}"            exit 1        fi        start_service "$2" "$3"        ;;    stop)        stop_service        ;;    edit_subpath)        if [ -z "$2" ]; then            echo -e "${red}Использование: $0 edit_subpath <NEW_SUBPATH>${NC}"            exit 1        fi        edit_subpath "$2"        ;;    *)        echo -e "${red}Использование: $0 {start <EXTERNAL_DOMAIN> <EXTERNAL_PORT> | stop | edit_subpath <NEW_SUBPATH>}${NC}"        exit 1        ;;esac